     1→import { API_BASE, API_VERSION, CACHE_TTL_MS, CACHE_MAX_ENTRIES, TRENDING_DAYS_BACK, TRENDING_CATEGORIES } from './constants.js';
     2→
     3→const RETRY_CONFIG = { MAX_RETRIES: 3, INITIAL_BACKOFF_MS: 1000, BACKOFF_MULTIPLIER: 2 };
     4→
     5→const getHeaders = () => {
     6→  const headers = {
     7→    'Accept': 'application/vnd.github+json',
     8→    'X-GitHub-Api-Version': API_VERSION
     9→  };
    10→  
    11→  const token = localStorage.getItem('gh-token');
    12→  if (token) {
    13→    headers['Authorization'] = `Bearer ${token}`;
    14→  }
    15→  
    16→  return headers;
    17→};
    18→
    19→const sleep = (ms) => new Promise(resolve => setTimeout(resolve, ms));
    20→
    21→const cache = new Map();
    22→
    23→const getCachedResponse = (url) => {
    24→  const cached = cache.get(url);
    25→  if (!cached) return null;
    26→  
    27→  if (Date.now() - cached.timestamp > CACHE_TTL_MS) {
    28→    cache.delete(url);
    29→    return null;
    30→  }
    31→  
    32→  console.log(`[Cache] HIT: ${url.substring(0, 60)}...`);
    33→  return cached.data;
    34→};
    35→
    36→const setCachedResponse = (url, data) => {
    37→  cache.set(url, { data, timestamp: Date.now() });
    38→  
    39→  if (cache.size > CACHE_MAX_ENTRIES) {
    40→    const firstKey = cache.keys().next().value;
    41→    cache.delete(firstKey);
    42→  }
    43→};
    44→
    45→/**
    46→ * Clears the internal API response cache
    47→ * @returns {void}
    48→ */
    49→export const clearCache = () => {
    50→  cache.clear();
    51→  console.log('[Cache] Cleared');
    52→};
    53→
    54→const fetchWithRetry = async (url, retries = RETRY_CONFIG.MAX_RETRIES, backoff = RETRY_CONFIG.INITIAL_BACKOFF_MS, useCache = true) => {
    55→  if (useCache) {
    56→    const cached = getCachedResponse(url);
    57→    if (cached) return cached;
    58→  }
    59→
    60→  try {
    61→    const response = await fetch(url, { headers: getHeaders() });
    62→    
    63→    if (response.status === 403) {
    64→      const remaining = response.headers.get('x-ratelimit-remaining');
    65→      const resetTime = response.headers.get('x-ratelimit-reset');
    66→      
    67→      if (remaining === '0') {
    68→        const resetDate = new Date(parseInt(resetTime) * 1000);
    69→        throw new Error(`Rate limit exceeded. Resets at ${resetDate.toLocaleTimeString()}`);
    70→      }
    71→      
    72→      throw new Error('Forbidden: Check your access token');
    73→    }
    74→    
    75→    if (response.status === 404) {
    76→      throw new Error('Resource not found');
    77→    }
    78→    
    79→    if (!response.ok) {
    80→      throw new Error(`HTTP ${response.status}: ${response.statusText}`);
    81→    }
    82→    
    83→    const result = {
    84→      data: await response.json(),
    85→      rateLimit: {
    86→        remaining: parseInt(response.headers.get('x-ratelimit-remaining') || '0'),
    87→        limit: parseInt(response.headers.get('x-ratelimit-limit') || '0'),
    88→        reset: parseInt(response.headers.get('x-ratelimit-reset') || '0')
    89→      }
    90→    };
    91→
    92→    if (useCache) {
    93→      setCachedResponse(url, result);
    94→    }
    95→
    96→    return result;
    97→  } catch (error) {
    98→    const isHttpError = error.message.startsWith('HTTP ') ||
    99→      error.message.includes('Rate limit') ||
   100→      error.message.includes('Resource not found') ||
   101→      error.message.includes('Forbidden');
   102→    
   103→    if (retries > 0 && !isHttpError) {
   104→      const jitter = Math.random() * 100;
   105→      await sleep(backoff + jitter);
   106→      return fetchWithRetry(url, retries - 1, backoff * RETRY_CONFIG.BACKOFF_MULTIPLIER, useCache);
   107→    }
   108→    throw error;
   109→  }
   110→};
