   112→/**
   113→ * Searches GitHub repositories with optional filters
   114→ * @param {string} query - Search query string
   115→ * @param {Object} [options={}] - Search options
   116→ * @param {string} [options.language=''] - Filter by programming language
   117→ * @param {number} [options.minStars=0] - Minimum star count filter
   118→ * @param {string} [options.sort='stars'] - Sort field (stars, forks, updated)
   119→ * @param {string} [options.order='desc'] - Sort order (asc, desc)
   120→ * @param {number} [options.page=1] - Page number for pagination
   121→ * @param {number} [options.perPage=30] - Results per page (max 100)
   122→ * @returns {Promise<{data: {items: Array, total_count: number}, rateLimit: {remaining: number, limit: number, reset: number}}>}
   123→ * @throws {Error} When API request fails after retries or rate limit exceeded
   124→ */
   125→export const searchRepositories = async (query, options = {}) => {
   126→  const {
   127→    language = '',
   128→    minStars = 0,
   129→    sort = 'stars',
   130→    order = 'desc',
   131→    page = 1,
   132→    perPage = 30
   133→  } = options;
   134→  
   135→  let q = query;
   136→  if (language) q += `+language:${language}`;
   137→  if (minStars > 0) q += `+stars:>=${minStars}`;
   138→  
   139→  const url = `${API_BASE}/search/repositories?q=${encodeURIComponent(q)}&sort=${sort}&order=${order}&page=${page}&per_page=${perPage}`;
   140→  
   141→  return fetchWithRetry(url);
   142→};
